package pages;import org.openqa.selenium.WebDriver;import org.openqa.selenium.WebElement;import org.openqa.selenium.support.FindBy;import org.openqa.selenium.support.PageFactory;import utilities.Driver;public class CalendarEventsPage {    WebDriver driver= Driver.getDriver();    public CalendarEventsPage(WebDriver driver) {        this.driver = driver;        PageFactory.initElements(driver, this);    }    @FindBy(xpath = "//h1[@class='oro-subtitle']")    public WebElement allCalendarEvents;    @FindBy(xpath = "//a[contains(@class,'btn main-group btn-primary pull-right')]")    public WebElement createEventBTN;    @FindBy(xpath = "//input[@class='select2-input']")    public WebElement createEventOwner;    @FindBy(id = "oro_calendar_event_form_title-uid-5d29588beba15")    public WebElement createEventTitle;    @FindBy(xpath = "//a[@class='btn add-list-item']")    public WebElement createEventReminder;    @FindBy(id = "date_selector_oro_calendar_event_form_start-uid-5d29588bebbe2")    public WebElement eventStartDate;    @FindBy(id = "time_selector_oro_calendar_event_form_start-uid-5d29588bebbe2")    public WebElement eventStartTime;    @FindBy(id = "date_selector_oro_calendar_event_form_end-uid-5d29588bebccb")    public WebElement eventEndDate;    @FindBy(id = "time_selector_oro_calendar_event_form_end-uid-5d29588bebccb")    public WebElement eventEndTime;    @FindBy(xpath = "//button[@class='btn btn-success action-button']")    public WebElement saveEvent;}