package stepDefinitions;import cucumber.api.java.en.And;import cucumber.api.java.en.Given;import cucumber.api.java.en.Then;import cucumber.api.java.en.When;import org.openqa.selenium.By;import org.openqa.selenium.WebDriver;import org.openqa.selenium.WebElement;import org.openqa.selenium.interactions.Actions;import org.testng.asserts.SoftAssert;import pages.*;import utilities.*;import java.util.concurrent.TimeUnit;public class Steps  {    public static WebDriver driver=Driver.getDriver();    private MenuOptionsPage menu = new MenuOptionsPage(driver);       private SoftAssert softAssert = new SoftAssert();    private VehiclesPage vehiclesPage = new VehiclesPage(driver);    private AccPage accPage = new AccPage(driver);    private CalendarEventsPage calendarEventsPage = new CalendarEventsPage(driver);    private CallsPage callsPage = new CallsPage(driver);    private ContactsPage contactsPage = new ContactsPage(driver);    private OpportunitiesPage opportunitiesPage = new OpportunitiesPage(driver);    private VyTrackDashboard vyTrackDashboard = new VyTrackDashboard(driver);    private Actions actions=new Actions(driver);    private VyTrackLoginPage vyTracklogin = new VyTrackLoginPage(driver);    @Given("^(.+) used (.+) and (.+) and click to log in button$")    public void used_and_and_click_to_log_in_button(String user, String username, String password) throws Throwable {        driver.get("http://qa1.vytrack.com/");        driver.manage().timeouts().implicitlyWait(3, TimeUnit.SECONDS);        vyTracklogin.login(username,password);        Thread.sleep(6000);    }    @When("^(.+) should land to home page with title being (.+)$")    public void should_land_to_home_page_with_title_being(String user, String titlelog) throws Throwable {        driver.manage().timeouts().implicitlyWait(3, TimeUnit.SECONDS);        softAssert.assertTrue(driver.getTitle().equals(titlelog),user+" title is "+titlelog);    }    @Then("^(.+) will navigate to (.+) module$")    public void will_navigate_to_module(String user, String module) throws Throwable {        driver.manage().timeouts().implicitlyWait(7, TimeUnit.SECONDS);        menu.module(module).click();}    @And("^Choose (.+) option$")    public void choose_option(String option) throws Throwable {        driver.manage().timeouts().implicitlyWait(7, TimeUnit.SECONDS);        menu.option(option).click();    }    @Then("^(.+) should be able to land to the (.+) page with title being (.+)$")    public void should_be_able_to_land_to_the_page_with_title_being(String user, String pages, String title) throws Throwable {        softAssert.assertTrue(driver.getTitle().equals(title),user+" title is "+title);        softAssert.assertTrue(driver.findElement(By.xpath("//h1[@class='oro-subtitle']")).isDisplayed(),pages+" is Displayed");    }    @Then("^Verify the name of the (.+) is displayed on top right and log out$")    public void verify_the_name_of_the_is_displayed_on_top_right_and_log_out(String user) throws Throwable {        driver.manage().timeouts().implicitlyWait(7, TimeUnit.SECONDS);        driver.navigate().back();        Thread.sleep(6000);        vyTrackDashboard.userMenu.click();        Thread.sleep(2000);        vyTrackDashboard.logOut.click();        Thread.sleep(6000);        softAssert.assertTrue(driver.findElement(By.xpath("//a[@class='dropdown-toggle']")).isDisplayed(),"Username in right top corner");        driver.findElement(By.className("dropdown-toggle")).click();        driver.findElement(By.linkText("Logout")).click();        softAssert.assertTrue(driver.getTitle().equals("Logout"),user+" Log out title");    }}