package steps;import cucumber.api.java.en.And;import cucumber.api.java.en.Given;import cucumber.api.java.en.Then;import cucumber.api.java.en.When;import org.openqa.selenium.WebDriver;import org.openqa.selenium.interactions.Actions;import org.testng.asserts.SoftAssert;import utilities.*;import javax.swing.*;public class Smoke_steps {    public static WebDriver driver;    private MenuOptionsPage menu = new MenuOptionsPage(driver);    private Actions actions = new Actions(driver);    private SoftAssert softAssert = new SoftAssert();    private VehiclesPage vehiclesPage = new VehiclesPage(driver);    private AccPage accPage = new AccPage(driver);    private CalendarEventsPage calendarEventsPage = new CalendarEventsPage(driver);    private CallsPage callsPage = new CallsPage(driver);    private ContactsPage contactsPage = new ContactsPage(driver);    private OpportunitiesPage opportunitiesPage = new OpportunitiesPage(driver);    private VyTrackDashboard vyTrackDashboard = new VyTrackDashboard(driver);    @When("^User will navigate to Fleet module$")    public void user_will_navigate_to_fleet_module() throws Throwable {        actions.moveToElement(menu.fleet).perform();    }    @Then("^User should be able to land to All Cars page with title being \"([^\"]*)\"$")    public void user_should_be_able_to_land_to_all_cars_page_with_title_being_something(String strArg1) throws Throwable {        softAssert.assertTrue(vehiclesPage.allCars.isDisplayed(), strArg1+" is Displayed");        softAssert.assertTrue(driver.getTitle().equals(strArg1), strArg1+" Title was matching ");        softAssert.assertAll();    }    @And("^Choose Vehicle option$")    public void choose_vehicle_option() throws Throwable {        menu.fleetVehicle.click();    }    @When("^User will navigate to Customers module$")    public void user_will_navigate_to_customers_module() throws Throwable {        actions.moveToElement(menu.customers).perform();    }    @Then("^User should be able to land to All Accounts page with title being \"([^\"]*)\"$")    public void user_should_be_able_to_land_to_all_accounts_page_with_title_being_something(String strArg1) throws Throwable {        softAssert.assertTrue(accPage.allAccountDrop.isDisplayed(), strArg1+" is Displayed");        softAssert.assertTrue(driver.getTitle().equals(strArg1), strArg1+" Title was matching ");        softAssert.assertAll();    }    @And("^Choose Accounts option from dropdown menu$")    public void choose_accounts_option_from_dropdown_menu() throws Throwable {        menu.customersAccount.click();    }    @Then("^User should be able to land to Contracts page with title being \"([^\"]*)\"$")    public void user_should_be_able_to_land_to_contracts_page_with_title_being_something(String strArg1) throws Throwable {        softAssert.assertTrue(contactsPage.allContacts.isDisplayed(), strArg1+" is Displayed");        softAssert.assertTrue(driver.getTitle().equals(strArg1), strArg1+" Title was matching ");        softAssert.assertAll();    }    @And("^Choose Contacts option from dropdown menu$")    public void choose_contacts_option_from_dropdown_menu() throws Throwable {        menu.customersContracts.click();    }    @When("^User will navigate to Activities module$")    public void user_will_navigate_to_activities_module() throws Throwable {        actions.moveToElement(menu.activities).click().perform();    }    @Then("^User should be able to land to All Calender Events page with title being \"([^\"]*)\"$")    public void user_should_be_able_to_land_to_all_calender_events_page_with_title_being_something(String strArg1) throws Throwable {        softAssert.assertTrue(calendarEventsPage.allCalendarEvents.isDisplayed(), strArg1+" is Displayed");        softAssert.assertTrue(driver.getTitle().equals(strArg1), strArg1+" Title was matching ");        softAssert.assertAll();    }    @And("^Choose Calender Events option from dropdown menu$")    public void choose_calender_events_option_from_dropdown_menu() throws Throwable {        menu.activitiesCalenderEvents.click();    }    @When("^User will navigate to Dashboard module$")    public void user_will_navigate_to_dashboard_module() throws Throwable {       actions.moveToElement(menu.dashboard).perform();    }    @Then("^User should be able to land to Dashboard page with title being \"([^\"]*)\"$")    public void user_should_be_able_to_land_to_dashboard_page_with_title_being_something(String strArg1) throws Throwable {        softAssert.assertTrue(vyTrackDashboard.dashHead.isDisplayed(), strArg1+" is Displayed");        softAssert.assertTrue(driver.getTitle().equals(strArg1), strArg1+"is as expected ");        softAssert.assertAll();    }    @And("^Choose Dashboard option$")    public void choose_dashboard_option() throws Throwable {        menu.dashboardDashboard.click();    }    @When("^User will navigate to Sales module$")    public void user_will_navigate_to_sales_module() throws Throwable {        actions.moveToElement(menu.sales).perform();    }    @Then("^User should be able to land to Open Opportunities page with title being \"([^\"]*)\"$")    public void user_should_be_able_to_land_to_open_opportunities_page_with_title_being_something(String strArg1) throws Throwable {        softAssert.assertTrue(opportunitiesPage.openOpportunities.isDisplayed(), strArg1+" is Displayed");        softAssert.assertTrue(driver.getTitle().equals(strArg1), strArg1+" is as expected ");        softAssert.assertAll();    }    @And("^Choose Open Opportunities option from dropdown menu$")    public void choose_open_opportunities_option_from_dropdown_menu() throws Throwable {        menu.salesOpportunities.click();    }}