package steps;import cucumber.api.java.en.And;import cucumber.api.java.en.Given;import cucumber.api.java.en.Then;import cucumber.api.java.en.When;import org.openqa.selenium.WebDriver;import org.testng.asserts.SoftAssert;import utilities.Driver;import utilities.Hooks;import utilities.VyTrackDashboard;import utilities.VyTrackLoginPage;public class Smoke_steps extends Hooks {    public static WebDriver driver;    @Given("^VyTrack login page$")    public void vytrack_login_page() throws Throwable {       driver= Driver.getDriver();       driver.get("http://qa1.vytrack.com/");}    @When("^User use valid driver credentials and login to the page$")    public void user_use_valid_driver_credentials_and_login_to_the_page() throws Throwable {        VyTrackLoginPage loginPage=new VyTrackLoginPage(driver);        loginPage.login("user156","UserUser123");        SoftAssert softAssert=new SoftAssert();        softAssert.assertTrue(driver.getTitle().equals("Dashboard"),"Driver was able to successfully login to the VyTrack");        softAssert.assertAll();    }}