package utilities;import org.openqa.selenium.WebDriver;import org.openqa.selenium.WebElement;import org.openqa.selenium.support.FindBy;import org.openqa.selenium.support.PageFactory;public class AccPage {       WebDriver driver;    public AccPage(WebDriver driver) {        this.driver = driver;        PageFactory.initElements(driver, this);    }    @FindBy(xpath = "//h1[@class='oro-subtitle']")    public WebElement allAccountDrop;    @FindBy(xpath = "//ul[@class='dropdown-menu']//a[contains(text(),'All')]")    public WebElement getAllAccountUnderDrop;    @FindBy(xpath = "//a[contains(@title,'export')]")    public WebElement exportGrid;}